+import { CSSProperties, ReactNode } from 'react';
+import fs from 'fs';
+import { contentpath } from './Paths';
+import { useFixedRandom, scale } from '@/utils/smallUtils';
+
+function walkDirectory(path: string, obj: any) {
+    let dir = fs.readdirSync(path);
+    for (let i = 0; i < dir.length; i++) {
+        let name = dir[i];
+        let target = path + '/' + name;
+        let stats = fs.statSync(target);
+        if (stats.isFile()) {
+            if (name.slice(-3) === '.md') {
+                obj[name.slice(0, -3)] = '';
+            }
+        } else if (stats.isDirectory()) {
+            obj[name] = {};
+            walkDirectory(target, obj[name]);
+        }
+    }
+}
+
+const WalkReactDir = ({ tree, level = 1 }: { tree: any; level?: number }) => {
+    const treekeys = Object.keys(tree);
+    return treekeys.map((key, i) => {
+        const style = { zIndex: `${treekeys.length - i}` };
+        if (typeof tree[key] === 'object') {
+            return (
+                <div
+                    className="nested"
+                    style={{
+                        left: `calc(0px - var(--left_offset_polaroid))`,
+                        ...style,
+                    }}
+                    key={i}
+                >
+                    <NavFrame
+                        style={{
+                            zIndex: '1000',
+                            left: `var(--left_offset_polaroid)`,
+                        }}
+                        pgName={key}
+                        key={i}
+                    ></NavFrame>
+                    <WalkReactDir tree={tree[key]} level={level + 1} />
+                </div>
+            );
+        }
+        return <NavFrame style={style} pgName={key} key={i}></NavFrame>;
+    });
+};
+
+export const NavColumn = async () => {
+    let tree = {};
+    walkDirectory(contentpath, tree);
+    console.log(tree);
+    return (
+        <nav>
+            <section>
+                <WalkReactDir tree={tree} />
+                <NavFrame
+                    pgName={
+                        <>
+                            Current timestamp: <br></br> 01.23.2024
+                        </>
+                    }
+                ></NavFrame>
+            </section>
+        </nav>
+    );
+};
+
+export const NavFrame = ({
+    children = '',
+    pgName = 'Victoria Hertel Velasco',
+    style = {},
+}: {
+    children?: ReactNode;
+    pgName?: ReactNode;
+    style?: CSSProperties;
+}) => {
+    return (
+        <div className="polaroid">
+            <div className="outer" style={style}>
+                <div className="image">{children}</div>
+                <div className="description">
+                    <span className="left">{pgName}</span>
+                </div>
+            </div>
+        </div>
+    );
+};"},{"sha":"b0b37693d480aea63c7d8a607fc12e4a5f7d6347","filename":"components/PageFrame.tsx","status":"modified","additions":1,"deletions":1,"changes":2,"blob_url":"https://github.com/vanghoa/victoria-hertel-site/blob/3077b87f77a6525d6a3f945c024a76d95cecde00/components%2FPageFrame.tsx","raw_url":"https://github.com/vanghoa/victoria-hertel-site/raw/3077b87f77a6525d6a3f945c024a76d95cecde00/components%2FPageFrame.tsx","contents_url":"https://api.github.com/repos/vanghoa/victoria-hertel-site/contents/components%2FPageFrame.tsx?ref=3077b87f77a6525d6a3f945c024a76d95cecde00","patch":"@@ -11,7 +11,7 @@ export const PageFrame = ({
