+import * as runtime from 'react/jsx-runtime';
+
+export default function PageServer({
+    children,
+    pgName = 'Victoria Hertel Velasco',
+    date = 'XX/XX/XXXX',
+}: {
+    children: ReactNode;
+    pgName?: string | ReactNode;
+    date?: string | ReactNode;
