+        );
+    }
+
+    const { default: MDX } = await run(code, {
+        Fragment: undefined,
+        ...runtime,
+        baseUrl: import.meta.url,
+    });
+
+    return <MDX components={{ ...MDXCustomComponents }} />;
+
+    console.log('refetch MDXContent');
+    return (
